    grid-row: 1 / 2;
    margin-left: 0px; }
  #port .back .title h2 {
    font-size: 25px; }
  #port .back .title p {
    font-size: 12px; }
  #port .back .box {
    grid-column: span 2;
    grid-row: 2 / 7;
    background: black; }
  #port .front {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-template-rows: repeat(8, 1fr);
    max-width: 1200px; }
  #port .front .image {
    grid-column: span 2;
    grid-row: 2 / 3; }
  #port .front .info {
    grid-column: span 2;
    grid-row: 3 / 6; }
  #port .front .image > div {
    margin-top: 50px;
    width: 80%; }
  #port .front .image > div:after {
    padding-top: 50%; }
  #port .front .call-to-action {
    bottom: auto; }
  #port .front .description {
    margin: 0;
    white-space: pre-wrap; }
  #port .front .description p {
    margin: 0 5px;
    white-space: pre-wrap; }
  #port .front .call-to-action {
    grid-column: span 2;
    grid-row: 3 / 5; }
  #port .front .call-to-action a {
    display: block;
    text-align: center;
    width: 50%;
    margin: 0px auto 5px auto; }
  #contact .wrapper {
    height: 90vh;
    grid-template-columns: repeat(8, 1fr);
    grid-template-rows: repeat(8, 1fr); }
  #contact .wrapper > div {
    grid-column: span 8;
    grid-row: span 2;
    height: auto; }
  #contact .left > h2 {
    display: none; }
  #contact .presentations {
    display: none; }
  #contact .white-splitter {
    left: -15vw;
    top: 20vh; }
  #contact .right {
    grid-row: span 4; }
  #contact .right h2 {
    font-size: 40px;
    margin-top: 40px;
    margin-bottom: 0px; } }
