/* ==========================================================================
   Hero
   ========================================================================== */
#hero .wrapper {
  display: grid;
  grid-template-columns: repeat(8, 1fr);
  grid-gap: 0px;
  width: 70vw;
  max-width: 860px;
  height: 50vmin;
  margin: auto;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  /*
     Scroll hint (mouse with moving scroller)
     ========================================================================== */ }
  #hero .wrapper .wrapper > div {
    position: relative; }
  #hero .wrapper .title {
    top: 0;
    bottom: 0;
    left: 0;
